    public static void registerBlock(String path, Block block) {
        Registry.register(Registry.BLOCK, new Identifier(Furnish.MOD_ID, path), block);
    }

    public static void registerBlocks() {
            for(int i = 0; i < woodtypeIds.length; i++) {
                    registerBlock("byg_" + woodtypeIds[i] + "_chair", woodenChairs[i]);
                    registerBlock("byg_" + woodtypeIds[i] + "_table", woodenTables[i]);
            }
    }
