    public static void registerItem(String path, Item item) {
        Registry.register(Registry.ITEM, new Identifier(Furnish.MOD_ID, path), item);
    }

    public static void registerItems() {
        for(int i = 0; i < BYGBlocks.woodtypeIds.length; i++) {
            registerItem("byg_" + BYGBlocks.woodtypeIds[i] + "_chair", woodenChairs[i]);
            registerItem("byg_" + BYGBlocks.woodtypeIds[i] + "_table", woodenTables[i]);
        }
    }
